package com.issayah.samplecode;

public class Statements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float a = 0f,b = 0f,c = 0f;
		b = a + 2f;
		//System.out.println(b);
		a = b * 4;
		//System.out.println(a);
		b = a / 3.14f;
		a = b - 8;
		a ++;
		//System.out.println(a);
		b --;
		c = (char)'K';
		//System.out.println(c);
		c = 'B';
		//System.out.println(c);
		// displays a number
	}

}
